mapper.select:
select 

from order_detail where 1 = 1 

    and order_id = #{orderId,jdbcType=VARCHAR}


    and product_id = #{productId,jdbcType=VARCHAR}


    and product_name = #{productName,jdbcType=VARCHAR}


    and product_model = #{productModel,jdbcType=VARCHAR}


    and product_spec = #{productSpec,jdbcType=VARCHAR}


    and group_price = #{groupPrice,jdbcType=DECIMAL}


    and product_price = #{productPrice,jdbcType=DECIMAL}


    and product_quantity = #{productQuantity,jdbcType=INTEGER}


    and product_icon = #{productIcon,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}

mapper.delete:

