mapper.select:
select 

from order_master where 1 = 1 

    and buyer_name = #{buyerName,jdbcType=VARCHAR}


    and buyer_phone = #{buyerPhone,jdbcType=VARCHAR}


    and buyer_address = #{buyerAddress,jdbcType=VARCHAR}


    and buyer_id = #{buyerId,jdbcType=VARCHAR}


    and need_invoice = #{needInvoice,jdbcType=SMALLINT}


    and invoice_type = #{invoiceType,jdbcType=SMALLINT}


    and groupon = #{groupon,jdbcType=SMALLINT}


    and groupon_id = #{grouponId,jdbcType=VARCHAR}


    and groupon_count = #{grouponCount,jdbcType=INTEGER}


    and title = #{title,jdbcType=VARCHAR}


    and tax_number = #{taxNumber,jdbcType=VARCHAR}


    and company_address = #{companyAddress,jdbcType=VARCHAR}


    and telephone = #{telephone,jdbcType=VARCHAR}


    and bank_name = #{bankName,jdbcType=VARCHAR}


    and bank_account = #{bankAccount,jdbcType=VARCHAR}


    and order_amount = #{orderAmount,jdbcType=DECIMAL}


    and order_status = #{orderStatus,jdbcType=TINYINT}


    and tracking_number = #{trackingNumber,jdbcType=VARCHAR}


    and delivery_code = #{deliveryCode,jdbcType=VARCHAR}


    and refund_trade_no = #{refundTradeNo,jdbcType=VARCHAR}


    and pay_trade_no = #{payTradeNo,jdbcType=VARCHAR}


    and pay_status = #{payStatus,jdbcType=TINYINT}


    and remark = #{remark,jdbcType=VARCHAR}


    and contract = #{contract,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}

