mapper.select:
select 

,

from product where 1 = 1 

    and price = #{price,jdbcType=DECIMAL}


    and group_price = #{groupPrice,jdbcType=DECIMAL}


    and cost = #{cost,jdbcType=DECIMAL}


    and is_default = #{isDefault,jdbcType=BIT}


    and stock = #{stock,jdbcType=INTEGER}


    and goods_sn = #{goodsSn,jdbcType=VARCHAR}


    and enable = #{enable,jdbcType=BIT}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and specification_values = #{specificationValues,jdbcType=LONGVARCHAR}

mapper.delete:

delete from product


      and price = #{price,jdbcType=DECIMAL}


      and group_price = #{groupPrice,jdbcType=DECIMAL}


      and cost = #{cost,jdbcType=DECIMAL}


