mapper.select:
select 

from user where 1 = 1 

    and username = #{username,jdbcType=VARCHAR}


    and ma_open_id = #{maOpenId,jdbcType=VARCHAR}


    and union_id = #{unionId,jdbcType=VARCHAR}


    and nickname = #{nickname,jdbcType=VARCHAR}


    and name = #{name,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and gender = #{gender,jdbcType=SMALLINT}


    and area_id = #{areaId,jdbcType=SMALLINT}


    and language = #{language,jdbcType=SMALLINT}


    and volunteer = #{volunteer,jdbcType=SMALLINT}


    and avatar = #{avatar,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and create_date = #{createDate,jdbcType=TIMESTAMP}

mapper.delete:

delete from user


      and username = #{username,jdbcType=VARCHAR}


      and ma_open_id = #{maOpenId,jdbcType=VARCHAR}


